import java.util.ArrayList;
import java.util.List;

public class EnhancedForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		List<String> ls = new ArrayList<String>();

		ls.add("a");
		ls.add("b");
		ls.add("c");
		ls.add("d");

		for (String s : ls) {
			System.out.println(s);
		}

	}

}
